  //
  //SCRIPT PARA LOS ULTIMOS COMENTARIOS EN EL HOME
  var url="";
  var html = '<ul class="t-item comment">';
  var admin1 = "SODA";
  var admin2 = "Jjdeath";
  var down_link = "LINKS CAIDOS.";

$.ajax({
  url: '/feeds/comments/default?published&alt=json-in-script&max-results=33&callback=?',
  type: 'get',
  dataType: "jsonp",
  success: function(data){
    for(var i=0; i<data.feed.entry.length; i++){
        for(var j=0; j<data.feed.entry[i].link.length; j++){
            if(data.feed.entry[i].link[j].rel=="alternate"){
                url=data.feed.entry[i].link[j].href;
                break;
            }
        }

        var isDeleted = data.feed.entry[i].gd$extendedProperty[0];

        if("content"in data.feed.entry[i]){
            var comment = data.feed.entry[i].content.$t;
        }
        else if("summary"in data.feed.entry[i]){
            var comment = data.feed.entry[i].summary.$t;
        }else{
            var comment = "";
        }

        var re = /<\S[^>]*>/g;
        comment = comment.replace(re, "");

        if(comment.length>60){
            comment = comment.substring(0,40);
        }               
        var name = data.feed.entry[i].author[0].name.$t;

        if(name == admin1 || name == admin2){
          html += '<li><span style="color: #00C853; font-weight: bold;"><i class="fa fa-code" aria-hidden="true"></i> '+name+':</span><a href="'+url+'" style=""> '+comment+'</a></li>';
        }else if(isDeleted.name === "blogger.contentRemoved" && isDeleted.value === "true"){
          html += '<li><span style="text-decoration: line-through; color: #F57F17;">'+name+': '+comment+'</span></li>';
        }else if(comment === down_link){
            html += '<li><span style="color: #ef5350; font-weight: bold;"><i class="fa fa-exclamation-triangle" aria-hidden="true"></i> BROKEN LINK: <a href="' + url + '">' + url + '</a></span></li>';
        }
        else{
          html+='<li><span>'+name+':</span><a href='+url+' style=""> '+comment+'</a></li>';
		}
    }
    html += '</ul>';
    document.getElementById('t-comment').innerHTML = html;
 }
});
